import ColorfulMessage from "./components/ColorfulMessage";
const App = () => {
  const onClickCountUp = () => {
    setNum(num + 1);
  };
  // [stateの値, state値を変更する関数名] = useState(初期値);
  const [num, setNum] = useState(0);
  return (
    <>
      <button onClick={onClickCountUp}>カウントアップ</button>
      <p>{num}</p>
    </>
  );
};

export default App;
