  return (
    <>
      <h1 style={{ color: "red" }}>こんにちは</h1>
      {/* color="red" message="元気です！"をPropsで渡す */}
      <ColorfulMessage color="red">元気です</ColorfulMessage>
      <button>on/off</button>
      <button onClick={onClickCountUp}>カウントアップ</button>
